import React from 'react';
import InputRange from 'react-input-range';

import 'react-input-range/lib/css/index.css';
import './range.css';

import Log from './../logarithmic/log';


// const logslider = (position) => {
//   // position will be between 0 and 100
//   var minp = 0;
//   var maxp = 100;

//   // The result should be between 100 an 10000000
//   var minv = Math.log(40);
//   var maxv = Math.log(9000);

//   // calculate adjustment factor
//   var scale = (maxv - minv) / (maxp - minp);

//   return Math.exp(minv + scale * (position - minp));
// }

// const calcPos = (pos) => {
//   if (pos === 0) return 0;
//   const val = logslider(pos);
//   if (val > 1000) return Math.round(val/100)*100;
//   if (val > 500) return Math.round(val/10)*10;
//   return Math.round(val);
// }


export default class Range extends React.Component {
  constructor(props) {
    super(props);
    this.state = { value: { min: 0, max: 100 } };
    this.logSlider = new Log({ minpos: 0, maxpos: 100, minval: 5, maxval: 1600 });
    this.onChange = this.onChange.bind(this);
    this.formatLabel = this.formatLabel.bind(this);
  }

  calcPos(pos) {
    if (pos === 0) return 0;
    const val = this.logSlider.value(pos);
    if (val > 1000) return Math.round(val / 100) * 100;
    if (val > 500) return Math.round(val / 10) * 10;
    return Math.round(val);
  }
  
  onChange(value) {
    this.setState({ value });
  }

  formatLabel(value) {
    return `Â£${this.calcPos(value)}`;
  }
  
  render() {
    return (
      <div>
      <RangeInput
       value={this.state.value} 
       onChange={this.onChange} 
       formatLabel={this.formatLabel}
      />
      </div>
    );
  }
}


const RangeInput = ({ value, onChange, formatLabel }) => (
  <InputRange
    step={1}
    formatLabel={formatLabel}
    maxValue={100}
    minValue={0}
    value={value}
    onChange={onChange}
    />
);
