import { Container, Row, Col } from 'mdbreact';
import React, { Component } from 'react';
import Carousel, { consts } from 'react-elastic-carousel';
import SlideComponent from '../components/js/SlideComponent';
import ZaloComponent from '../components/js/ZaloComponent';
import QuantityIncrementButton from '../components/js/QuantityIncrementButton';
import { MDBCard, MDBCardImage, MDBCardText, MDBCardBody } from 'mdbreact';
import { MDBRow, MDBCol, MDBContainer } from 'mdbreact';
import { MDBRipple } from 'mdb-react-ui-kit';
import CarouselPage from '../components/js/TestModule';
import { useNavigate, useSearchParams } from "react-router-dom";

const SanPham = () => {
  let navigate = useNavigate();
  const [searchParams, setSearchParams] = useSearchParams();

  fetch("https://server-spring-boot-api.herokuapp.com/api/v1/bathroomaccessories?page=1&itemsperpage=12")
    .then(res => res.json())
    .then(
      (result) => {
        //alert(this.props.match.params.id);
        //alert(result[0]);
        //this.setState({ maxPage: Math.floor(result.length / this.state.itemsPerPage) + 1 });
        //this.setState({ dataArray: result });
      },
      // Note: it's important to handle errors here
      // instead of a catch() block so that we don't swallow
      // exceptions from actual bugs in components.
      (error) => {
        alert(error);
      }
    )


  alert(searchParams.get("test"));
  return (
    <div>
      <ZaloComponent />

      <div class="d-flex justify-content-center mb-5" style={{ height: "60vh" }}>
        <div className='col-lg-8 bg-light d-flex align-content-center flex-wrap rounded-3' style={{ borderRadius: "5px" }}>
          <div className='container'>
            <div className='row justify-content-center'>
              <div className='col-lg-4'>
                <div className='d-flex justify-content-center'>
                  <img src='https://mdbootstrap.com/img/new/standard/city/053.webp' className='w-100' />
                </div>
              </div>
              <div className='col-lg-8 text-dark'>
                <div className='d-flex justify-content-center text-uppercase'>
                  <div>
                    <h3>Bộ phụ kiện vuông</h3>

                    <h6>Thương hiệu : RIWASU</h6>
                    <h6>
                      Tình trạng : <span className='text-success'>Còn hàng</span>
                    </h6>
                    <div onClick={event => {
                      navigate("/san-pham?test=1");
                    }}>Đặt mua và liên hệ</div>
                    {/*<QuantityIncrementButton/>
                            <div className='d-flex justify-content-start'>
                              <div className='bg-success p-2 mr-1'>Mua hàng</div>
                              <div className='bg-success p-2'>Thêm vào giỏ hàng</div>
                            </div>*/}
                    <div className='mt-2'>
                      Quý khách xem mô tả sản phẩm bên dưới !
                    </div>
                  </div>

                </div>

              </div>
            </div>
            <div>
              <div className='mt-3'>
                <h3>Thông tin sản phẩm</h3>
              </div>
              <div>
                <p>Riwasu xin giới thiệu tới quý khách hàng bộ phụ kiện phòng tắm vuông</p>
                <p>Với thiết kế vuông vắn, chắc chắn và sang trọng, sản phẩm hứa hẹn sẽ đem lại cho bạn không gian phòng tắm trông thật đẳng cấp và bắt mắt. Đây sẽ là nơi đem lại cảm giác thoải mãi
                  cho bạn sau những giờ làm việc căng thẳng !
                </p>
                <p>
                  Sản phẩm được làm hoàn toàn bằng chất liệu INOX 304 không gỉ chính hãng 100%. Với chất liệu này bạn có thể yên tâm sử dụng sản phẩm mà không lo lắng về
                  vấn đề hoăn gỉ, ố vàng trong thời gian sử dụng !
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>



    </div>
  );

};

export default SanPham;
