import React from "react";

import Slider from "./Slider";


export default function Header() {
  const [value, setValue] = React.useState([400, 1000]);
  const [key, setKey] = React.useState(null);

  // To re-trigger the animation, we need to unmount and remount
  // the element. We'll do that by dynamically changing its
  // "key" prop, telling React that it needs to recreate it.
  useRandomInterval(() => setKey(Math.random()), ...value);

  return (
    <div className="App">
      <h1>Heartbeat Demo</h1>
      <div className="popper" key={key} />

      <Slider
        pearling
        minDistance={10}
        min={100}
        max={1000}
        onChange={(value) => setValue(value)}
      />
    </div>
  );
}

const random = (min, max) => Math.floor(Math.random() * (max - min)) + min;

const useRandomInterval = (callback, minDelay, maxDelay) => {
  const timeoutId = React.useRef(null);
  const savedCallback = React.useRef(callback);

  React.useEffect(() => {
    savedCallback.current = callback;
  });

  React.useEffect(() => {
    if (typeof minDelay === "number" && typeof maxDelay === "number") {
      const handleTick = () => {
        const nextTickAt = random(minDelay, maxDelay);

        timeoutId.current = window.setTimeout(() => {
          savedCallback.current();
          handleTick();
        }, nextTickAt);
      };

      handleTick();
    }

    return () => window.clearTimeout(timeoutId.current);
  }, [minDelay, maxDelay]);

  function cancel() {
    window.clearTimeout(timeoutId.current);
  }

  return cancel;
};
