import { Container, Row, Col } from 'mdbreact';
import React, { Component } from 'react';
import Carousel, { consts } from 'react-elastic-carousel';
import SlideComponent from '../components/js/SlideComponent';
import ZaloComponent from '../components/js/ZaloComponent';
import QuantityIncrementButton from '../components/js/QuantityIncrementButton';
import { MDBCard, MDBCardImage, MDBCardText, MDBCardBody } from 'mdbreact';
import { MDBRow, MDBCol, MDBContainer } from 'mdbreact';
import { MDBRipple } from 'mdb-react-ui-kit';
import CarouselPage from '../components/js/TestModule';
import ReactSlider from 'react-slider';
import CustomizedSlider from '../components/js/Slider';
import { Slider, value } from '@mui/material/Slider';
import Checkbox from '@mui/material/Checkbox';
import { yellow, red } from '@mui/material/colors';
import Pagination from '@mui/material/Pagination';
import Stack from '@mui/material/Stack';
import { useParams, Navigate } from 'react-router-dom'

function getParams() {
    const id = useParams();
    return id;
}
class PhuKienPhongTam extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            dataArray: [], maxPage: 1, itemsPerPage: 12, statusCodeReturn: 200, dataArrayTemp: [],
            priceIncreaseCheckbox: false, priceDecreaseCheckbox: false, mostViewCheckbox: false
        };
        //const [searchParams, setSearchParams] = useSearchParams();
        //const {token} = useParams();
    }

    componentDidMount() {
        //const response = await fetch('server-spring-boot-api.herokuapp.com/api/CheckKey111');
        //fetch("server-spring-boot-api.herokuapp.com/api/CheckKey")


        //alert(searchParams.get("test"));
        fetch("https://server-spring-boot-api.herokuapp.com/api/v1/bathroomaccessories?page=1&itemsperpage=12")
            .then(res => res.json())
            .then(
                (result) => {
                    //alert(this.props.match.params.id);
                    //alert(result[0]);
                    this.setState({ maxPage: Math.floor(result.length / this.state.itemsPerPage) + 1 });
                    this.setState({ dataArray: result });
                },
                // Note: it's important to handle errors here
                // instead of a catch() block so that we don't swallow
                // exceptions from actual bugs in components.
                (error) => {
                    alert(error);
                }
            )
    }
    render() {
        const label = { inputProps: { 'aria-label': 'Checkbox demo' } };
        const handleTest = (event) => {
            alert(event.target)
        }
        const handleFilter = (event) => {
            let minRange = document.getElementById("thumb1").value;
            let maxRange = document.getElementById("thumb2").value;
            let result;
            fetch("https://server-spring-boot-api.herokuapp.com/api/v1/bathroomaccessories?minRange=" + minRange + "&maxRange=" + maxRange)
                .then(res => {
                    this.state.statusCodeReturn = res.status;
                    return res.json()
                })
                .then(
                    (result) => {
                        //alert(this.props.match.params.id);
                        //alert(result[0]);
                        alert(result.length);
                        alert(this.state.statusCodeReturn);
                    },
                    // Note: it's important to handle errors here
                    // instead of a catch() block so that we don't swallow
                    // exceptions from actual bugs in components.
                    (error) => {
                        alert(error);
                    }
                )
            if (this.state.statusCodeReturn == 200) {
                //this.setState({ dataArray: this.state.dataArrayTemp });
                //this.forceUpdate();
            }
        }
        const handleChangePage = (event, newPage) => {
            fetch("https://server-spring-boot-api.herokuapp.com/api/v1/bathroomaccessories?page=" + newPage + "&itemsperpage=12")
                .then(res => res.json())
                .then(
                    (result) => {
                        //alert(this.props.match.params.id);
                        //alert(result[0]);
                        this.setState({ dataArray: result });
                        this.forceUpdate();
                    },
                    // Note: it's important to handle errors here
                    // instead of a catch() block so that we don't swallow
                    // exceptions from actual bugs in components.
                    (error) => {
                        alert(error);
                    }
                )
        };
        return (
            <div>
                <ZaloComponent />

                <div class="d-flex justify-content-center mb-5" style={{ height: "" }}>
                    <div className='col-lg-10'>
                        <div className='d-flex justify-content-center mb-5'>
                            <form className='col-lg-2 text-dark bg-light mr-5' style={{ height: "100%", borderRadius: "5px" }}>
                                <div className='d-flex justify-content-center mt-1'>
                                    <div className='border border-primary p-1' style={{ borderRadius: "3px" }}>LỌC SẢN PHẨM</div>
                                </div>

                                <div className='mt-2' style={{ borderStyle: "dotted" }}>
                                    <div>
                                        <Checkbox className='ml-0' {...label} defaultChecked={this.state.priceIncreaseCheckbox} onClick={handleTest} color="success" />
                                        <span>Giá tăng dần</span>

                                    </div>
                                    <div>
                                        <Checkbox
                                            {...label}
                                            defaultChecked={this.state.priceDecreaseCheckbox}
                                            sx={{
                                                color: yellow[800],
                                                '&.Mui-checked': {
                                                    color: yellow[600],
                                                },
                                            }}
                                        />
                                        <span>Giá giảm dần</span>

                                    </div>
                                    <div>
                                        <Checkbox
                                            {...label}
                                            defaultChecked={this.state.mostViewCheckbox}
                                            sx={{
                                                color: red[800],
                                                '&.Mui-checked': {
                                                    color: red[600],
                                                },
                                            }}
                                        />
                                        <span>Xem nhiều</span>

                                    </div>
                                </div>

                                <div>Khoảng giá</div>
                                <CustomizedSlider />
                                <br></br>
                                <div class="btn btn-primary mb-1" onClick={handleFilter}>Lọc</div>
                                <div className='col-lg-10'></div>
                            </form>
                            <div className='danh-sac-san-pham-bg col-lg-10 text-dark'>
                                <div className='d-flex justify-content-center mt-1 mb-2'>
                                    <div className='border border-primary p-1 bg-light' style={{ borderRadius: "3px" }}>DANH SÁCH SẢN PHẨM</div>
                                </div>

                                <div>
                                    <div className='col-lg-12'>
                                        <div class="row justify-content-start">
                                            {this.state.dataArray.map(item => (
                                                <div class="col-lg-3 mb-5 d-flex align-items-center justify-content-center">
                                                    <a href='san-pham' style={{ textDecoration: 'none', color: "black" }}>
                                                        <div className='shake-effect bg-light'>

                                                            <img src={item.imagelink} className='' style={{ width: "300px", height: "300px" }} />
                                                            <div className='text-uppercase text-center'>{item.name}</div>
                                                            <h6 className='text-uppercase text-center'>{item.newprice}</h6>
                                                            <h6 className='text-uppercase text-center text-danger'><s>{item.oldprice}</s></h6>
                                                        </div>
                                                    </a>

                                                </div>
                                            ))}

                                        </div>
                                        <div className='d-flex justify-content-center mb-2'>
                                            <Stack spacing={2}>
                                                <Pagination count={this.state.maxPage} variant="outlined" onChange={handleChangePage} />
                                            </Stack>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>

                    </div>

                </div>



            </div>
        )
    }
}


export default PhuKienPhongTam;
