import React from "react";
import { useState } from "react";
import "../css/Slider.css"
class Header extends React.Component {
    constructor()
    {
        super();
        this.updateInput2 = this.updateInput2.bind(this);
        this.updateInput1 = this.updateInput1.bind(this);
        this.state = { input1Width : "40", maxValueInput1 : "400000", input2Width : "10%", value1 : "200000", value2 : "100000", value3 : "90%", maxValue : "100000", minValue : "200000", minRange : "200000", maxRange : "1000000"}
    }

    // Max range  = range1 + min range + range2

    updateInput1(event)
    {
        this.setState({value1 : event.target.value})
        if (this.state.value1 == this.state.maxValueInput1)
        {
            var temp = this.state.input1Width + 1;
            this.setState({input1Width : temp});
        }
    }
    updateInput2(event)
    {
        //var temp1 = (this.state.maxRange - this.state.value2 - this.state.minRange) / this.state.maxRange * 100;
        //temp1 = temp1 + "%";
        this.setState({value2 : event.target.value})
        var temp = parseInt((this.state.maxRange - this.state.value1 - this.state.minRange) / this.state.maxRange * 100);
        this.setState({input2Width : temp});
        if (parseInt(this.state.maxRange) - parseInt(this.state.value1) - parseInt(this.state.value2) < parseInt(this.state.minRange) )
        {
            var temp2 = this.state.maxRange - this.state.value1 - this.state.minRange;
            var input1Width = parseInt((this.state.value2 / this.state.maxRange) * 100);
            this.setState({value2 : 200000});
        }
    }

    render() {
      return (
        <div className="">
            <div className="wrapper">
                <input className="firstDiv" style={{width : this.state.input1Width + "%"}}  value={this.state.value1} name="range" type="range" id="customRange1" min={0} max={this.state.maxValueInput1} step={10} onChange={this.updateInput1} />
                <input className="secondDiv" style={{width : "100%", color : "red"}} value={this.state.value2} name="range" type="range" id="customRange1" min={0} max={this.state.maxRange} step={10} onChange={this.updateInput2} />
            </div>
            <br></br>
            <div className="float-left">Gi√°</div>
            <div className="float-right">Abc</div>
            <div id="multi-ranges-tooltips" data-tooltip="true"></div>
            
            <div>{this.state.value1}</div>
        </div>
      );
    }
  }
  
  export default Header;
